var animesJSON = require('../db.json');


//home
exports.home = function(req, res) {
    
    var anime = animesJSON.anime;

    res.render('home', {
        title : "Anime",
        title2 : "Wars",
        title3 : "Welcome to my blog",
        anime : anime

    });
  };

  /////anime single

  exports.anime_single = function(req, res) {

      var episode_number = req.params.episode_number;

      var animes = animesJSON.animes;

      var anime = animes[episode_number - 1];

      var title = anime.title;

      res.render('anime_single', { 
          animes : animes,
          title : title
         
      });
  };



  ////not found

  exports.notFound = function(req, res) {
     res.send("This is not the page that you are looking for");
  };